import React, { useState, useEffect, useCallback } from 'react';
import { Vector2D, Controls } from '../types';

export const usePlayerControls = (): Controls => {
  const [keys, setKeys] = useState<Set<string>>(new Set());
  const [mousePosition, setMousePosition] = useState<Vector2D>({ x: 0, y: 0 });
  const [isMouseDown, setIsMouseDown] = useState(false);

  const handleKeyDown = useCallback((e: KeyboardEvent) => {
    setKeys(prevKeys => new Set(prevKeys).add(e.key.toLowerCase()));
  }, []);

  const handleKeyUp = useCallback((e: KeyboardEvent) => {
    setKeys(prevKeys => {
      const newKeys = new Set(prevKeys);
      newKeys.delete(e.key.toLowerCase());
      return newKeys;
    });
  }, []);

  const handleMouseMove = useCallback((e: MouseEvent) => {
    setMousePosition({ x: e.clientX, y: e.clientY });
  }, []);

  const handleMouseDown = useCallback(() => {
    setIsMouseDown(true);
  }, []);

  const handleMouseUp = useCallback(() => {
    setIsMouseDown(false);
  }, []);

  useEffect(() => {
    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);
    window.addEventListener('mousemove', handleMouseMove);
    window.addEventListener('mousedown', handleMouseDown);
    window.addEventListener('mouseup', handleMouseUp);

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keyup', handleKeyUp);
      window.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('mousedown', handleMouseDown);
      window.removeEventListener('mouseup', handleMouseUp);
    };
  }, [handleKeyDown, handleKeyUp, handleMouseMove, handleMouseDown, handleMouseUp]);

  return { keys, mouse: { position: mousePosition, isDown: isMouseDown } };
};
