import React, { useState, useEffect, useRef, useCallback } from 'react';
import { LevelData, Player, Enemy, Bullet, Platform, AmmoPack, Vector2D, DynamicGameObject, NinjaType, GameObject } from '../types';
import { useGameLoop } from '../hooks/useGameLoop';
import { usePlayerControls } from '../hooks/usePlayerControls';
import { PLAYER_HORIZONTAL_SPEED, PLAYER_JUMP_FORCE, PLAYER_HEALTH, PLAYER_WIDTH, PLAYER_HEIGHT, BULLET_SPEED, BULLET_SIZE, NINJA_STATS, SHOOT_COOLDOWN, GRAVITY, MAX_FALL_SPEED, FRICTION } from '../constants';

interface GameScreenProps {
  levelData: LevelData;
  initialAmmo: number;
  initialScore: number;
  onLevelComplete: (score: number) => void;
  onGameOver: (score: number) => void;
  updateStats: (stats: { health: number; ammo: number; score: number }) => void;
}

const GameScreen: React.FC<GameScreenProps> = ({ levelData, initialAmmo, initialScore, onLevelComplete, onGameOver, updateStats }) => {
  const [player, setPlayer] = useState<Player>({
    id: 'player',
    position: levelData.initialPlayerPosition,
    width: PLAYER_WIDTH,
    height: PLAYER_HEIGHT,
    velocity: { x: 0, y: 0 },
    angle: 0,
    facing: 'right',
    health: PLAYER_HEALTH,
    onGround: false,
  });
  const [enemies, setEnemies] = useState<Enemy[]>(() =>
    levelData.enemies.map((z, i) => ({
      id: `enemy-${i}-${Date.now()}`,
      position: z.position,
      type: z.type,
      velocity: { x: 0, y: 0 },
      facing: 'left',
      onGround: false,
      ...NINJA_STATS[z.type],
    }))
  );
  const [bullets, setBullets] = useState<Bullet[]>([]);
  const [platforms] = useState<Platform[]>(() =>
    levelData.platforms.map((o, i) => ({
      id: `platform-${i}-${Date.now()}`,
      ...o,
    }))
  );
  const [ammoPacks, setAmmoPacks] = useState<AmmoPack[]>(() =>
    levelData.ammoPacks.map((a, i) => ({
      id: `ammo-${i}-${Date.now()}`,
      position: a.position,
      amount: a.amount,
      width: 30,
      height: 30,
    }))
  );
  const [ammo, setAmmo] = useState(initialAmmo);
  const [score, setScore] = useState(initialScore);
  const lastShotTime = useRef(0);
  const gameAreaRef = useRef<HTMLDivElement>(null);
  const controls = usePlayerControls();

  const checkAABBCollision = (obj1: GameObject, obj2: GameObject) => {
    return (
        obj1.position.x < obj2.position.x + obj2.width &&
        obj1.position.x + obj1.width > obj2.position.x &&
        obj1.position.y < obj2.position.y + obj2.height &&
        obj1.position.y + obj1.height > obj2.position.y
    );
  };

  const applyPhysics = useCallback((obj: DynamicGameObject, deltaTime: number): DynamicGameObject => {
      let newObj = { ...obj };

      // Specters ignore gravity
      if ('type' in newObj && newObj.type === NinjaType.Specter) {
        // No gravity for specters
      } else {
        newObj.velocity.y += GRAVITY;
        if (newObj.velocity.y > MAX_FALL_SPEED) newObj.velocity.y = MAX_FALL_SPEED;
      }

      let nextPos = {
        x: newObj.position.x + newObj.velocity.x * (deltaTime / 16.67),
        y: newObj.position.y + newObj.velocity.y * (deltaTime / 16.67)
      };

      newObj.onGround = false;

      // Platform collision for non-specters
      if (!('type' in newObj && newObj.type === NinjaType.Specter)) {
          for (const platform of platforms) {
              const isColliding = checkAABBCollision({ ...newObj, position: nextPos }, platform);
              if (isColliding) {
                  const prevBottom = newObj.position.y + newObj.height;
                  const isFalling = newObj.velocity.y > 0;

                  if (isFalling && prevBottom <= platform.position.y) {
                      nextPos.y = platform.position.y - newObj.height;
                      newObj.velocity.y = 0;
                      newObj.onGround = true;
                  } 
                  else {
                      if ((newObj.velocity.x > 0 && newObj.position.x + newObj.width <= platform.position.x) || (newObj.velocity.x < 0 && newObj.position.x >= platform.position.x + platform.width)) {
                        nextPos.x = newObj.position.x;
                        newObj.velocity.x = 0;
                         if ('facing' in newObj && newObj.type === NinjaType.Patroller) {
                             (newObj as Enemy).facing = (newObj as Enemy).facing === 'left' ? 'right' : 'left';
                         }
                      }
                      if(newObj.velocity.y < 0 && newObj.position.y >= platform.position.y + platform.height){
                         nextPos.y = newObj.position.y;
                         newObj.velocity.y = 0;
                      }
                  }
              }
          }
      }

      newObj.position = nextPos;

      newObj.position.x = Math.max(0, Math.min(levelData.mapDimensions.width - newObj.width, newObj.position.x));
      if (newObj.position.y > levelData.mapDimensions.height) {
          if ('health' in newObj) newObj.health = 0;
      }

      return newObj;
  }, [platforms, levelData.mapDimensions]);

  const gameLoopCallback = useCallback((deltaTime: number) => {
    const gameArea = gameAreaRef.current;
    if (!gameArea) return;

    // --- Player Logic ---
    setPlayer(p => {
      let newPlayer = { ...p };
      if (controls.keys.has('a') || controls.keys.has('arrowleft')) {
          newPlayer.velocity.x = -PLAYER_HORIZONTAL_SPEED;
          newPlayer.facing = 'left';
      } else if (controls.keys.has('d') || controls.keys.has('arrowright')) {
          newPlayer.velocity.x = PLAYER_HORIZONTAL_SPEED;
          newPlayer.facing = 'right';
      } else {
          newPlayer.velocity.x *= FRICTION;
          if (Math.abs(newPlayer.velocity.x) < 0.1) newPlayer.velocity.x = 0;
      }

      if ((controls.keys.has('w') || controls.keys.has('arrowup')) && newPlayer.onGround) {
          newPlayer.velocity.y = -PLAYER_JUMP_FORCE;
          newPlayer.onGround = false;
      }

      const rect = gameArea.getBoundingClientRect();
      const playerScreenX = rect.left + rect.width / 2;
      const playerScreenY = rect.top + rect.height / 2;
      newPlayer.angle = Math.atan2(controls.mouse.position.y - playerScreenY, controls.mouse.position.x - playerScreenX);

      return applyPhysics(newPlayer, deltaTime) as Player;
    });

    // --- Shooting ---
    const now = Date.now();
    if (controls.mouse.isDown && ammo > 0 && now - lastShotTime.current > SHOOT_COOLDOWN) {
      lastShotTime.current = now;
      setAmmo(a => a - 1);
      setBullets(b => [...b, {
        id: `bullet-${now}`,
        position: {x: player.position.x + player.width/2, y: player.position.y + player.height/2},
        width: BULLET_SIZE,
        height: BULLET_SIZE,
        angle: player.angle
      }]);
    }

    // --- Update Bullets ---
    setBullets(prevBullets => {
      return prevBullets.map(bullet => ({
          ...bullet,
          position: {
            x: bullet.position.x + Math.cos(bullet.angle) * BULLET_SPEED,
            y: bullet.position.y + Math.sin(bullet.angle) * BULLET_SPEED,
          }
      })).filter(b => 
        b.position.x > 0 && b.position.x < levelData.mapDimensions.width &&
        b.position.y > 0 && b.position.y < levelData.mapDimensions.height &&
        !platforms.some(p => checkAABBCollision(b, p))
      );
    });

    // --- Update Enemies ---
    setEnemies(prevEnemies => {
      let newBullets = [...bullets];
      let newScore = score;
      const updatedEnemies = prevEnemies.map(enemy => {
        let newEnemy = { ...enemy };

        const directionToPlayerX = player.position.x - newEnemy.position.x;
        const directionToPlayerY = player.position.y - newEnemy.position.y;
        const distance = Math.sqrt(directionToPlayerX * directionToPlayerX + directionToPlayerY * directionToPlayerY);

        switch(newEnemy.type) {
            case NinjaType.Chaser:
            case NinjaType.Brute:
            case NinjaType.Oni:
                newEnemy.velocity.x = Math.sign(directionToPlayerX) * newEnemy.speed;
                newEnemy.facing = Math.sign(directionToPlayerX) > 0 ? 'right' : 'left';
                break;
            case NinjaType.Specter:
                newEnemy.velocity.x = (directionToPlayerX / distance) * newEnemy.speed;
                newEnemy.velocity.y = (directionToPlayerY / distance) * newEnemy.speed;
                newEnemy.facing = Math.sign(directionToPlayerX) > 0 ? 'right' : 'left';
                break;
            case NinjaType.Patroller:
                if (newEnemy.onGround) {
                    newEnemy.velocity.x = newEnemy.facing === 'right' ? newEnemy.speed : -newEnemy.speed;
                }
                break;
        }

        newEnemy = applyPhysics(newEnemy, deltaTime) as Enemy;

        if (checkAABBCollision(player, newEnemy)) {
          setPlayer(p => ({ ...p, health: Math.max(0, p.health - 0.5) }));
        }

        return newEnemy;
      }).filter(enemy => {
        if (enemy.health <= 0) return false;
        const hitBulletIndex = newBullets.findIndex(b => checkAABBCollision(b, enemy));
        if (hitBulletIndex > -1) {
          enemy.health -= 1;
          newBullets.splice(hitBulletIndex, 1);
          if (enemy.health <= 0) {
            newScore += enemy.value;
            return false;
          }
        }
        return true;
      });
      setBullets(newBullets);
      setScore(newScore);
      return updatedEnemies;
    });

    // --- Ammo pack collision ---
    setAmmoPacks(prevPacks => {
        return prevPacks.filter(pack => {
            if(checkAABBCollision(player, pack)) {
                setAmmo(a => a + pack.amount);
                return false;
            }
            return true;
        })
    });
  }, [controls, player, ammo, score, bullets, levelData, platforms, applyPhysics]);

  useGameLoop(gameLoopCallback);

  useEffect(() => {
    updateStats({ health: Math.round(player.health), ammo, score });
    if (player.health <= 0) {
      onGameOver(score);
    } else if (enemies.length === 0) {
      onLevelComplete(score);
    }
  }, [player.health, ammo, score, enemies.length, onGameOver, onLevelComplete, updateStats]);

  const viewportWidth = gameAreaRef.current?.clientWidth || window.innerWidth;
  const viewportHeight = gameAreaRef.current?.clientHeight || window.innerHeight;

  const worldStyle = {
    '--world-x': `${-(player.position.x - viewportWidth / 2)}px`,
    '--world-y': `${-(player.position.y - viewportHeight / 2)}px`,
  } as React.CSSProperties;

  return (
    <div ref={gameAreaRef} className="w-full h-full bg-gray-900 cursor-crosshair overflow-hidden relative" style={worldStyle}>
      <div className="absolute top-0 left-0 transition-transform duration-100 ease-linear" style={{ transform: `translate(var(--world-x), var(--world-y))` }}>
        <div className="absolute bg-grid-pattern" style={{ width: levelData.mapDimensions.width, height: levelData.mapDimensions.height, background: 'linear-gradient(to bottom, #2c3e50, #34495e)'}}></div>

        {platforms.map(o => (
          <div key={o.id} className="absolute bg-gray-700 border-b-4 border-gray-800" style={{'--x': `${o.position.x}px`, '--y': `${o.position.y}px`, '--w': `${o.width}px`, '--h': `${o.height}px`, left: 'var(--x)', top: 'var(--y)', width: 'var(--w)', height: 'var(--h)'} as React.CSSProperties}></div>
        ))}

        {ammoPacks.map(a => (
            <div key={a.id} className="absolute flex items-center justify-center text-3xl" style={{ '--x': `${a.position.x}px`, '--y': `${a.position.y}px`, '--w': `${a.width}px`, '--h': `${a.height}px`, left: 'var(--x)', top: 'var(--y)', width: 'var(--w)', height: 'var(--h)' } as React.CSSProperties}>
                ðŸ’°
            </div>
